<?php
class News
{
    private $news_id;
    private $title;
    private $content;
    private $publish_date;
    private $link_img;
    private $category_id;

    public function __construct($news_id, $title, $content, $publish_date, $link_img, $category_id)
    {
        $this->news_id = $news_id;
        $this->title = $title;
        $this->content = $content;
        $this->publish_date = $publish_date;
        $this->link_img = 'data:image/jpeg;base64,';
        $this->category_id = $category_id;
    }

    public function getNewsID()
    {
        return $this->news_id;
    }

    public function setNewsID($news_id)
    {
        $this->news_id = $news_id;
    }

    public function getTitle()
    {
        return $this->title;
    }

    public function setTitle($title)
    {
        $this->title = $title;
    }

    public function getContent()
    {
        return $this->content;
    }

    public function setContent($content)
    {
        $this->content = $content;
    }

    public function getPublishDate()
    {
        return $this->publish_date;
    }

    public function setPublishDate($publish_date)
    {
        $this->publish_date = $publish_date;
    }

    public function getLinkImg()
    {
        return $this->link_img;
    }

    public function setLinkImg($link_img)
    {
        $this->link_img = $link_img;
    }

    public function getCategoryID()
    {
        return $this->category_id;
    }

    public function setCategoryID($category_id)
    {
        $this->category_id = $category_id;
    }
}